def is_happy(s):
    if len(s) < 3:
        return False
    else:
        distinct_count = 0
        for i in range(len(s)-2):
            if s[i:i+3] == s[i+1] or s[i:i+3] == s[i+2]:
                distinct_count += 1
        return distinct_count == 3

def below_threshold(l: list, t: int) -> bool:
    if not isinstance(l, list):
        raise ValueError("Input must be a list")
    if len(l) < 1:
        return False
    if l[0] >= t:
        return False
    for i in range(1, len(l)):
        if l[i] >= t:
            return False
    return True
